import {BASE_URL} from "../constants/urls"
import axios from "axios"

export const login = (body, clear) => {
    axios.post(`${BASE_URL}/user/login`, body)
    .then((res)=> {
        localStorage.setItem("token", res.data.token)
        clear()
    })
    .catch((err)=> alert("Erro no login"))
}
