import React from "react"
import {ScreenContainer, LogoImage, InputsContainer} from "styled-components"
import TextField from "@material-ui/core/TextField"
import useForm from "modulo1/cookenu/src/hooks/useForm.js"
import Button from "@material-ui/core/Button"

const LoginPage = () => {
    const [form, onChange, clear] = useForm({email:"", password:""})


    const onSubmitForm = () => {

    }

    return (
        <ScreenContainer>
            <h1>LoginPage</h1>
            <InputsContainer>
                <form onSubmit= {onSubmitForm}>
                    <TextField 
                        name={"email"}
                        value={form.email}
                        onChange={onChange}
                        label={"Email"}
                        variant={"outlined"}
                        fullWidth
                        margin={"normal"}
                        required
                        type={"email"}
                    />

                    <TextField 
                        name={"password"}
                        value={form.password}
                        onChange={onChange}
                        label={"Senha"}
                        variant={"outlined"}
                        fullWidth
                        margin={"normal"}
                        required
                        type={"password"}
                    />

                    <Button
                        type={"submit"}
                        fullWidth
                        variant={"contained"}
                        color={"primary"}
                        margin={"normal"}
                    >
                        Fazer Login!
                    </Button> 
                </form>
            </InputsContainer>
        </ScreenContainer>
    )
}

export default LoginPage
